                         <div className="form-group">
                          <TextField
                                id="categoryName"
                                label="New Category Name"
                                margin="normal"
                                variant="outlined"
                                inputProps={{ "aria-label": "bare" }}
                                name="name"
                                onBlur={handleBlur}
                                onChange={handleChange}
