EnhancedTableToolbar.propTypes = {
  numSelected: PropTypes.number.isRequired,
  selected:PropTypes.array,
  allCategories:PropTypes.func,
  handleunSelect:PropTypes.func
};
const useStyles = makeStyles(theme => ({
  root: {
    width: '100%',
    marginTop: theme.spacing(0),
  },
  paper: {
    width: '100%',
    marginTop: theme.spacing(0),

    // marginBottom: theme.spacing(2),
  },
  table: {
    minWidth: 750,
  },
  tableWrapper: {
    overflowX: 'auto',
  },
}));

function Category(props) {
  const classes = useStyles();
  const [loading, setLoading] = useState(false);
  const [loadingButtonStyle, setLoadingButtonStyle] = useState({
    paddingRight: "2.5rem"
  });
  const enableLoading = () => {
    setLoading(true);
    setLoadingButtonStyle({ paddingRight: "3.5rem" });
  };

  const disableLoading = () => {
    setLoading(false);
    setLoadingButtonStyle({ paddingRight: "2.5rem" });
  };
  const [order, setOrder] = React.useState('asc');
  const [orderBy, setOrderBy] = React.useState('calories');
  const [selected, setSelected] = React.useState([]);
  const [page, setPage] = React.useState(0);
  const [dense, setDense] = React.useState(false);
  const [rowsPerPage, setRowsPerPage] = React.useState(5);
  const [remove, setRemove] = React.useState(false);
  const [edit, setEdit] = React.useState(false);
  const [removesnack, setRemovesnack] = React.useState(false);
  const [curname,setCurname]= React.useState("");
  const [curid,setCurid]= React.useState("");
  const [curdescription,setCurdescription]= React.useState("");
  const [allinfo,setAllinfo] = React.useState([]);
  useEffect(() => {
    setAllinfo(props.allcategories);
  }, [props])
  function handleEditClose() {
    setEdit(false);
  }
  function handleEditClick(id,name, description){
    setEdit(true);
    setCurid(id);
    setCurname(name);
    setCurdescription(description);
  }

  function handleRemoveClose() {
    setRemove(false);
  }
  function handleRemoveClick(id,name, description){
    setRemove(true);
    setCurname(name);
    setCurid(id);
    setCurdescription(description);
  }
  function handleRemoveSnackClick(){
    enableLoading();
    setTimeout(() => {
      actions.deleteCategory(curid)
        .then(res => {
          disableLoading();
          let {data} = res;
          //console.log('===  delete category  == ')
          //console.log(res)
          if(!data.success) {
          }
          else{
            handleRemoveClose();
            setRemovesnack(true);
            props.allCategories(data.allcategories);
          }
        })
        .catch(() => {
        });
    }, 1000);
  }
  function handleRemoveSnackClose(){
    setRemovesnack(false);
  }
  function handleRequestSort(event, property) {
    const isDesc = orderBy === property && order === 'desc';
    setOrder(isDesc ? 'asc' : 'desc');
    setOrderBy(property);
  }

  function handleSelectAllClick(event) {
    if (event.target.checked) {
      const newSelecteds = allinfo.map(n => n._id);
      setSelected(newSelecteds);
      return;
    }
    setSelected([]);
  }
  function handleunSelect(){
    setSelected([]);
  }

  function handleClick(event, name) {
    const selectedIndex = selected.indexOf(name);
    let newSelected = [];

    if (selectedIndex === -1) {
      newSelected = newSelected.concat(selected, name);
    } else if (selectedIndex === 0) {
      newSelected = newSelected.concat(selected.slice(1));
    } else if (selectedIndex === selected.length - 1) {
      newSelected = newSelected.concat(selected.slice(0, -1));
    } else if (selectedIndex > 0) {
      newSelected = newSelected.concat(
        selected.slice(0, selectedIndex),
        selected.slice(selectedIndex + 1),
      );
    }

    setSelected(newSelected);
  }

  function handleChangePage(event, newPage) {
    setPage(newPage);
  }

  function handleChangeRowsPerPage(event) {
    setRowsPerPage(+event.target.value);
  }

  function handleChangeDense(event) {
    setDense(event.target.checked);
  }

  const isSelected = name => selected.indexOf(name) !== -1;

  const emptyRows = rowsPerPage - Math.min(rowsPerPage, allinfo.length - page * rowsPerPage);

  return (
    <div className={classes.root}>
      <Paper className={classes.paper}>
        <EnhancedTableToolbar numSelected={selected.length}
        selected={selected} 
        allCategories={props.allCategories}
        handleunSelect={handleunSelect}
        />
        <div className={classes.tableWrapper}>
          <Table
            className={classes.table}
            aria-labelledby="tableTitle"
            size={dense ? 'small' : 'medium'}
          >
            <EnhancedTableHead
              numSelected={selected.length}
              order={order}
              orderBy={orderBy}
              onSelectAllClick={handleSelectAllClick}
              onRequestSort={handleRequestSort}
              rowCount={allinfo.length}
            />
            <TableBody>
              {stableSort(allinfo, getSorting(order, orderBy))
                .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                .map((row, index) => {
                  const isItemSelected = isSelected(row._id);
                  const labelId = `enhanced-table-checkbox-${index}`;

                  return (
                    <TableRow
                      hover

                      role="checkbox"
                      aria-checked={isItemSelected}
                      tabIndex={-1}
                      key={row._id}
                      selected={isItemSelected}
                    >
                      <TableCell padding="checkbox">
                        <Checkbox
                          checked={isItemSelected}
                          onClick={event => handleClick(event, row._id)}
                          inputProps={{ 'aria-labelledby': labelId }}
                        />
                      </TableCell>
                      <TableCell component="th" id={labelId} scope="row" padding="none">
                        {index+1}
                      </TableCell>
                      <TableCell align="center">{row.name}</TableCell>
                      <TableCell align="right">
                          <IconButton aria-label="EditIcon" onClick={()=>handleEditClick(row._id,row.name,row.description)} style={{paddingTop:0,paddingBottom:0}}>
                            <EditIcon />
                          </IconButton>
                          <IconButton aria-label="DeleteIcon" onClick={()=>handleRemoveClick(row._id,row.name,row.description)} style={{paddingTop:0,paddingBottom:0}}>
                            <DeleteIcon />
                          </IconButton>
                      </TableCell> 
                    </TableRow>
                  );
                })}
              {emptyRows > 0 && (
                <TableRow style={{ height: 49 * emptyRows }}>
                  <TableCell colSpan={6} />
                </TableRow>
              )}
            </TableBody>
          </Table>
        </div>
        <TablePagination
          rowsPerPageOptions={[5, 10, 25]}
          component="div"
          count={allinfo.length}
          rowsPerPage={rowsPerPage}
          page={page}
          backIconButtonProps={{
            'aria-label': 'Previous Page',
          }}
          nextIconButtonProps={{
            'aria-label': 'Next Page',
          }}
          onChangePage={handleChangePage}
          onChangeRowsPerPage={handleChangeRowsPerPage}
        />
      </Paper>
      {/* Delete Dialog */}
      <Dialog
        open={remove}
        onClose={handleRemoveClose}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">{"Delete Alert?"}</DialogTitle>
        <DialogContent>
          <DialogContentText id="alert-dialog-description">
            Do you want to delete this Category field?
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleRemoveClose} color="primary">
            Disagree
          </Button>
          <Button onClick={handleRemoveSnackClick} color="primary" autoFocus className={`btn btn-primary btn-elevate kt-login__btn-primary ${clsx(
                      {
                        "kt-spinner kt-spinner--right kt-spinner--md kt-spinner--light": loading
                      }
                    )}`}>
            Agree
          </Button>
        </DialogActions>
      </Dialog>
      {/* Edit Dialog */}
      <Dialog open={edit} onClose={handleEditClose} aria-labelledby="form-dialog-title" fullWidth={true}>
          <Formik
                  initialValues={{
                    name: curname,
                    description: curdescription
                  }}
                  validate={values => {
                    const errors = {};
                    if (!values.name) {
                      errors.name = 'You have to input correctly'
                    }
                    if (!values.description) {
                      errors.description = 'You have to input correctly'
                    }
                    return errors;
                  }}
                  onSubmit={(values, { setStatus, setSubmitting }) => {
                    //console.log('===  data 1 == ')
                    enableLoading();
                    setTimeout(() => {
                      actions.updateCategory({'_id':curid,'name':values.name,'description':values.description})
                        .then(res => {
                          disableLoading();
                          let {data} = res;
                          //console.log('===  data  == ')
                          //console.log(res)
                          if(!data.success) {
                            setSubmitting(false)
                            setStatus(
                              data.errMessage
                            );
                            return;
                          }
                          else{
                            props.allCategories(data.allcategories)
                            handleEditClose();
                          }
                        })
                        .catch(() => {
                          //console.log('===  data2  == ')
                          disableLoading();
                          setSubmitting(false);
                          setStatus(
                            'Error!!! you have to check your Database or Connection'
                          );
                        });
                    }, 1000);
                  }}
                >
                  {({
                    values,
                    status,
                    errors,
                    touched,
                    handleChange,
                    handleBlur,
                    handleSubmit,
                    isSubmitting
                  }) => (
                      <form
                        noValidate={true}
                        autoComplete="off"
                        className="kt-form"
                        onSubmit={handleSubmit}
                      >
              <DialogTitle id="form-dialog-title">Edit Category</DialogTitle>

              <DialogContent dividers>
                        {status ? (
                          <div role="alert" className="alert alert-danger">
                            <div className="alert-text">{status}</div>
                          </div>
                        ) : (
                          <div></div>
                        )}
                         <div className="form-group">
                          <TextField
                                id="categoryName"
                                label="New Category Name"
                                margin="normal"
                                variant="outlined"
                                inputProps={{ "aria-label": "bare" }}
                                name="name"
                                onBlur={handleBlur}
                                onChange={handleChange}
                                value={values.name}
                                helperText={touched.name && errors.name}
                                error={Boolean(touched.name && errors.name)}
                            />
                          </div>
                          <div className="form-group">
                            <TextField
                                id="contactIdMsg"
                                label="Category Description"
                                multiline
                                rows="4"
                                margin="normal"
                                name="description"
                                variant="outlined"
                                onBlur={handleBlur}
                                onChange={handleChange}
                                value={values.description}
                                helperText={touched.description && errors.description}
                                error={Boolean(touched.description && errors.description)}
                            />
                          </div>

              </DialogContent>
              <DialogActions>
                <Button onClick={handleEditClose} color="primary">
                  Cancel
                </Button>
                <Button color="primary"type="submit" disabled={isSubmitting}className={`btn btn-primary btn-elevate kt-login__btn-primary ${clsx(
                      {
                        "kt-spinner kt-spinner--right kt-spinner--md kt-spinner--light": loading
                      }
                    )}`}>
                  Update
                </Button>
              </DialogActions>
              </form>
                  )}
                </Formik>
      </Dialog> 
      <Snackbar
        anchorOrigin={{
          vertical: 'top',
          horizontal: 'right',
        }}
        open={removesnack}
        autoHideDuration={6000}
        onClose={handleRemoveSnackClose}
      >
        <MySnackbarContentWrapper
          onClose={handleRemoveSnackClose}
          variant={"success"}
          message="Removed Successfully!"
        />
      </Snackbar>
    </div>
  );
}

